int longestSubstrDistinctChars (string S)
{
    // your code here
    unordered_map<char,int> mp;
    int len=0,i=0;
    while(i<S.length()){
        if(mp.count(S[i]) == 0){
            mp.insert({S[i],i});
            i++;
        }
        else{
            int tmp=mp.size();
            len=max(len,tmp);
            i=mp[S[i]]+1;
            mp.clear();
        }
    }
    if(len<mp.size()){
        len=mp.size();
    }
    return len;
}

